mapper.select:
select

from ef_popads_info

	
		and task_name = #{taskName,jdbcType=VARCHAR}
	
	
		and task_status = #{taskStatus,jdbcType=VARCHAR}
	
	
		and model_id = #{modelId,jdbcType=INTEGER}
	

order by update_time desc 

	limit #{start},#{pageSize}

select count(*) from ef_popads_info

	
		and task_name = #{taskName,jdbcType=VARCHAR}
	
	
		and task_status = #{taskStatus,jdbcType=VARCHAR}
	
	
		and model_id = #{modelId,jdbcType=INTEGER}
