mapper.select:
SELECT product.id, product.product_name, product.product_type, price.price, price.discount_price, product.product_term, product.product_instruction, product.remark FROM ef_flow_product product, ef_product_price price WHERE product.id = price.product_id
and price.user_nation_code = #{userNationCode,jdbcType=VARCHAR}
and price.product_nation_code = #{buyNationCode,jdbcType=VARCHAR}
and product.product_type = #{productType,jdbcType=VARCHAR}
limit #{start},#{pageSize}

SELECT count(*) FROM ef_flow_product product, ef_product_price price WHERE product.id = price.product_id
and price.user_nation_code = #{userNationCode,jdbcType=VARCHAR}
and price.product_nation_code = #{buyNationCode,jdbcType=VARCHAR}
and product.product_type = #{productType,jdbcType=VARCHAR}
